mapper.update:
update sys_resource
set state = 0
where id = #{id,jdbcType=INTEGER}

mapper.select:
select

from sys_resource
where state = 1

    and text LIKE "%"{text,jdbcType=VARCHAR}"%"


    and type LIKE "%"#{type,jdbcType=VARCHAR}"%"


    and parent_id = #{parentId,jdbcType=INTEGER}


    and href = #{href,jdbcType=VARCHAR}


    and iconCls = #{iconcls,jdbcType=VARCHAR}


    and permission = #{permission,jdbcType=VARCHAR}


    and sort_no = #{sortNo,jdbcType=INTEGER}


    and is_show = #{isShow,jdbcType=INTEGER}


    and create_by = #{createBy,jdbcType=INTEGER}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and update_by = #{updateBy,jdbcType=INTEGER}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}
